mapper.update:
update org_user_role
set user_id = #{userId,jdbcType=BIGINT},
role_id = #{roleId,jdbcType=BIGINT},
gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, user_id, role_id, gmt_create
from org_user_role
where id = #{id,jdbcType=BIGINT}
